"use strict";var canvas=document.getElementById("canvas"),ctx=canvas.getContext("2d");ctx.canvas.width=window.innerWidth,ctx.canvas.height=window.innerHeight;var particleArray=[],colours=["#c489ff","#a3d6cc","#a4a8d4","#88bfbf"],maxSize=40,minSize=0,mouseRadius=80,mouse={x:null,y:null};function Particle(i,t,e,s,n,a){this.x=i,this.y=t,this.directionX=e,this.directionY=s,this.size=n,this.colour=a}function init(){particleArray=[];for(var i=0;i<2e3;i++){var t=Math.random()*innerWidth+0,e=Math.random()*innerWidth+0,s=.2*Math.random()-.1,n=.2*Math.random()-.1,a=colours[Math.floor(Math.random()*colours.length)];particleArray.push(new Particle(t,e,s,n,0,a))}}function animate(){requestAnimationFrame(animate),ctx.clearRect(0,0,innerWidth,innerHeight);for(var i=0;i<particleArray.length;i++)particleArray[i].update()}window.addEventListener("mousemove",function(i){mouse.x=i.x,mouse.y=i.y,console.log(mouse)}),Particle.prototype.draw=function(){ctx.beginPath(),ctx.arc(this.x,this.y,this.size,0,2*Math.PI,!1),ctx.fillStyle=this.colour,ctx.fill()},Particle.prototype.update=function(){(this.x+2*this.size>canvas.width||this.x-2*this.size<0)&&(this.directionX=-this.directionX),(this.y+2*this.size>canvas.height||this.y-2*this.size<0)&&(this.directionY=-this.directionY),this.x+=this.directionX,this.y+=this.directionY,mouse.x-this.x<mouseRadius&&mouse.x-this.x>-mouseRadius&&mouse.y-this.y<mouseRadius&&mouse.y-this.y>-mouseRadius?this.size<maxSize&&(this.size+=5):this.size>minSize&&(this.size-=.1),this.size<0&&(this.size=0),this.draw()},init(),animate(),window.addEventListener("resize",function(){canvas.width=innerWidth,canvas.height=innerHeight,init()});